package biz.neustar.hopper.record;

import java.util.Date;

import biz.neustar.hopper.message.DClass;
import biz.neustar.hopper.message.DNSSEC;
import biz.neustar.hopper.message.Name;
import biz.neustar.hopper.message.Type;
import biz.neustar.hopper.record.impl.SIGBase;

/**
 * Signature - A SIG provides the digital signature of an RRset, so that the
 * data can be authenticated by a DNSSEC-capable resolver. The signature is
 * usually generated by a key contained in a KEYRecord
 * 
 * @see RRSet
 * @see DNSSEC
 * @see KEYRecord
 * 
 * @author Brian Wellington
 */

public class SIGRecord extends SIGBase {

    private static final long serialVersionUID = 4963556060953589058L;

    public SIGRecord() {
    }

    protected Record getObject() {
        return new SIGRecord();
    }

    /**
     * Creates an SIG Record from the given data
     * 
     * @param covered
     *            The RRset type covered by this signature
     * @param alg
     *            The cryptographic algorithm of the key that generated the
     *            signature
     * @param origttl
     *            The original TTL of the RRset
     * @param expire
     *            The time at which the signature expires
     * @param timeSigned
     *            The time at which this signature was generated
     * @param footprint
     *            The footprint/key id of the signing key.
     * @param signer
     *            The owner of the signing key
     * @param signature
     *            Binary data representing the signature
     */
    public SIGRecord(Name name, DClass any, long ttl, int covered, DNSSEC.Algorithm alg,
            long origttl, Date expire, Date timeSigned, int footprint,
            Name signer, byte[] signature) {
        super(name, Type.SIG, any, ttl, covered, alg, origttl, expire,
                timeSigned, footprint, signer, signature);
    }

}
